(1, 'Floor Plans', NULL, 'Skylight comprises of four (4) separate areas; Beach Bar, 360 Lounge, Pool Deck, and Lower Terrace. Each section will provide guests with difference types of seating, views, and atmospheres.', NULL, 'http://skylightnhatrang.com/imateq/www/anh-2.jpg', NULL, '<span style=\"color:rgb(74, 74, 74)\">Skylight comprises of four (4) separate areas; Beach Bar, 360 Lounge, Pool Deck, and Lower Terrace. Each section will provide guests with difference types of seating, views, and atmospheres.</span>', NULL, '2015-04-07 10:39:12', '2015-04-07 11:03:19', 'true', 'block', 'http://skylightnhatrang.com/imateq/www/anh-2.jpg', 'Opening April 2015', NULL, 0, NULL, NULL, NULL, NULL),
(2, '360° Sky Deck', NULL, 'Skylight is the highest open 360˚ skydeck in Vietnam, perched on top of the 45th floor of the Best Western Premier Havana Nha Trang. It is the first attraction of its kind in Nha Trang with unrivaled views encompassing the entire beach and beyond.\r\n\r\nCome take a #SkylightSelfie from this one-of-a-kind destination!', NULL, 'http://skylightnhatrang.com/imateq/www/anh-1.jpg', NULL, '<p class=\"gettext\" style=\"text-align:justify\">Skylight is the highest open 360˚ skydeck in Vietnam, perched on top of the 45th floor of the Best Western Premier Havana Nha Trang. It is the first attraction of its kind in Nha Trang with unrivaled views encompassing the entire beach and beyond.</p>\r\n\r\n<p class=\"gettext mt20\" style=\"text-align:justify\">Come take a&nbsp;<a href=\"http://skylightnhatrang.com/index.html#\" style=\"box-sizing: border-box; color: rgb(0, 0, 0); text-decoration: none;\">#SkylightSelfie</a>&nbsp;from this one-of-a-kind destination!</p>\r\n', NULL, '2015-04-07 11:00:48', '2015-04-07 21:20:45', 'true', 'block', 'http://skylightnhatrang.com/imateq/www/anh-1.jpg', 'Opening April 2015', NULL, 0, NULL, NULL, NULL, NULL),
(3, 'Skywalk', NULL, 'Ever wonder what it feels like to be 43 floors above ground? Now you can experience the thrill of stepping out onto Skylight’s glass skywalk for an adrenaline pumping rush. We dare you!', NULL, 'http://skylightnhatrang.com/imateq/www/anh-3.jpg', NULL, '<span style=\"color:rgb(74, 74, 74)\">Ever wonder what it feels like to be 43 floors above ground? Now you can experience the thrill of stepping out onto Skylight&rsquo;s glass skywalk for an adrenaline pumping rush. We dare you!</span>', NULL, '2015-04-07 11:04:28', NULL, 'true', 'block', NULL, '', NULL, 0, NULL, NULL, NULL, NULL),
(4, 'Lighthouse', NULL, 'Standing tall and shining as a beacon for Nha Trang Bay, Skylight is home to the world’s tallest lighthouse. It can be spotted from anywhere in the city.', NULL, 'http://skylightnhatrang.com/imateq/www/anh-2.jpg', NULL, '<span style=\"color:rgb(74, 74, 74)\">Standing tall and shining as a beacon for Nha Trang Bay, Skylight is home to the world&rsquo;s tallest lighthouse. It can be spotted from anywhere in the city.</span>', NULL, '2015-04-07 11:04:59', '2015-04-07 11:05:02', 'true', 'block', NULL, '', NULL, 0, NULL, NULL, NULL, NULL),
(5, 'Rooftop Beach Club', NULL, 'This world-class venue will soon be Vietnam’s first rooftop beach club with handcrafted specialty cocktails, ice-cold draught beers, international cuisine, and next level entertainment.\r\n\r\nDesigned and operated by international development company, See The World Group (Los Angeles), Skylight is setting out to make Nha Trang a must visit destination in Southeast Asia.', NULL, 'http://skylightnhatrang.com/imateq/www/anh-4.jpeg', NULL, '<a class=\"opening\" href=\"http://skylightnhatrang.com/index.html#\" style=\"box-sizing: border-box; color: rgb(0, 0, 0); text-decoration: none; font-size: 20px; display: block; margin-bottom: 20px; font-family: Oswald, sans-serif; line-height: 22px;\">Opening Late Summer 2015</a>\r\n<p class=\"gettext\" style=\"text-align:justify\">This world-class venue will soon be Vietnam&rsquo;s first rooftop beach club with handcrafted specialty cocktails, ice-cold draught beers, international cuisine, and next level entertainment.</p>\r\n\r\n<p class=\"gettext mt20\" style=\"text-align:justify\">Designed and operated by international development company, See The World Group (Los Angeles), Skylight is setting out to make Nha Trang a must visit destination in Southeast Asia.</p>\r\n', NULL, '2015-04-07 11:05:39', NULL, 'true', 'block', NULL, 'Opening Late Summer 2015', NULL, 0, NULL, NULL, NULL, NULL),
